  }

  return (
    <div className="tableList">
      <table>
        <thead>
          <tr>
            <th>No</th>
            <th>Nama</th>
            <th>Jumlah</th>
            <th>Harga</th>
          </tr>
        </thead>
        <tbody>
          {medicine.map((medicine,index) => (
            <tr key={medicine.id}>
              <th>{index+1}</th>
              <th>{medicine.namaObat}</th>
              <th>{medicine.jumlahObat}</th>
              <th>{medicine.hargaObat}</th>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}

export default DataObat;
