import axios from "axios";
import React, { useEffect, useState } from "react";
import { Obat } from '../../../Server/models/Obat';
import index from '../pages/penjualan/index';


const DataObat:React.FC = () => {
  const [obatList, setObatList] = useState();

  const url='http://localhost:3001/obat/'

  useEffect(()=>{
    getObat()
  },[])

  const getObat = async () => {
    const response = await axios.get(url)
    setObatList(response.data)
  }

  return (
    <div className="tableList">
      <table>
        <thead>
          <tr>
            <th>No</th>
            <th>Nama</th>
            <th>Jumlah</th>
            <th>Harga</th>
          </tr>
        </thead>
        <tbody>
          {obatList.map((obat,index) => (
            <tr key={obat.id}>
            <th>{index+1}</th>
            <th>{obat.namaObat}</th>
            <th>{obat.jumlahObat}</th>
            <th>{obat.hargaObat}</th>
          </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}

export default DataObat;
