    <div className="inputPenjualan">
      <table className="tablePenjualan">
        <thead>
          <th>Nama Obat</th>
          <th>Stok Tersedia</th>
          <th>Jumlah Obat</th>
          <th>Aksi</th>
        </thead>
        <tbody>
          {medicine && medicine.map((list, index) => (
            <tr>
              <td>
                <select className="SelectBox">
                    <option value={list.namaObat}>{list.namaObat}</option>
